import { useState, useEffect } from "react";

import useDebounce from "./useDebounce";

export function useSearch(data: any[], setData: any, searchData: any) {
	// Состояние и сеттер состояния для поискового запроса
	const [searchValue, setSearchValue] = useState("");

	const debouncedSearchTerm = useDebounce(searchValue, 500);
	//!============================================

	function searchingHandler(searchingValue) {
		const value = searchingValue.toLowerCase().trim();
		searchData(value);
	}

	function clearSearch() {
		if (searchValue !== "") {
			setData("");
			searchData("");
		}
	}

	//?===================================================
	useEffect(() => {
		// Убедиться что у нас есть значение (пользователь ввел что-то)
		if (debouncedSearchTerm.length > 0) {
			return searchingHandler(debouncedSearchTerm.toLowerCase());
		} else {
			clearSearch();
		}
	}, [debouncedSearchTerm]);
	//?===================================================

	return {
		setSearchValue,
		clearSearch,
	};
}
