import { usePopup } from "../../../../../hooks/usePopup";
import TableHead from "./components/TableHead";
import RowTable from "./components/RowTable";
import PopUp from "../../../../Modals/PopUp/PopUp";

import style from "./Table.module.scss";

export default function Table({
  columns,
  hasCheckbox,
  data,
  setModalState,
  checkboxSetting,
}) {
  const {
    allClick,
    isAllChecked,
    checkboxHandler,
    deleteCellTable,
    checkedItemsArray,
  } = checkboxSetting;
  const { openPopup, PopUpToggle } = usePopup({ checkedItemsArray });
  return (
    <>
      <table className={style["content-sales-table"]}>
        <TableHead
          hasCheckbox={hasCheckbox}
          allClick={allClick}
          isChecked={isAllChecked}
          theadList={columns.map((i) => i.name)}
        />
        <tbody className={style["content-sales-table__body"]}>
          {data.map((item, index) => {
            const isChecked = checkedItemsArray.includes(item.id);
            return (
              <RowTable
                hasCheckbox={hasCheckbox}
                key={`id-${index}${Math.random()}`}
                item={item}
                columns={columns}
                setModalState={setModalState}
                checkboxHandler={checkboxHandler}
                isChecked={isChecked}
                idModal={"purchaseEdit"}
                data={data}
              />
            );
          })}
        </tbody>
      </table>
      {openPopup && (
        <PopUp
          openPopup={openPopup}
          PopUpToggle={PopUpToggle}
          checkedItemsArray={checkedItemsArray}
          deleteCellTable={deleteCellTable}
        ></PopUp>
      )}
    </>
  );
}
